mapper.select:
SELECT  uuid, name, origin,producer,unitUuid,inPrice,outPrice,goodsTypeUuid
FROM tbl_goods
WHERE uuid=#{uuid}

SELECT  uuid, name
FROM tbl_unit
ORDER BY uuid;

SELECT  uuid, name
FROM tbl_unit
ORDER BY uuid;

mapper.update:
UPDATE tbl_goods
name = #{name}, origin = #{origin}, producer = #{producer}, unitUuid = #{unitUuid}, inPrice = #{inPrice}, outPrice = #{outPrice}, goodsTypeUuid = #{goodsTypeUuid},
WHERE uuid = #{uuid}
